// src/pages/SwipeProject.js
import '../styles/SwipeProject.css';

import React from 'react';
import { motion, useMotionValue, useTransform, useAnimation } from 'framer-motion';

const SwipeProject = () => {
  const card1X = useMotionValue(0);
  const card1Y = useMotionValue(0);

  const card2X = useMotionValue(300); // Initial offset for the second card
  const card2Y = useMotionValue(0);

  const card1RotateX = useTransform(card1X, [-100, 100], [-25, 25]);
  const card1RotateY = useTransform(card1Y, [-100, 100], [-25, 25]);
  const card1Opacity = useTransform(card1X, [-100, 100], [1, 0.5]);

  const card2RotateX = useTransform(card2X, [-100, 100], [-25, 25]);
  const card2RotateY = useTransform(card2Y, [-100, 100], [-25, 25]);
  const card2Opacity = useTransform(card2X, [-100, 100], [1, 0.5]);

  const card1Animation = useAnimation();
  const card2Animation = useAnimation();

  const handleCard1DragEnd = () => {
    // If card1 is swiped left or right, move card2 to the center
    if (Math.abs(card1X.get()) > 100) {
      card2Animation.start({
        x: 0,
        transition: { type: "spring", stiffness: 300, damping: 30 }
      });
    }

    // Reset card1's position
    card1Animation.start({
      x: 0,
      y: 0,
      transition: { type: "spring", stiffness: 300, damping: 30 }
    });
  };

  return (
    <div className="swipe-project">
      <h1>Swipe to Match with Projects</h1>
      <h3>Swipe left for rejecting and right for viewing more project details</h3>
      <motion.div
        className="card"
        drag
        dragConstraints={{ left: -100, right: 100, top: -100, bottom: 100 }}
        style={{ x: card1X, y: card1Y, rotateX: card1RotateX, rotateY: card1RotateY, opacity: card1Opacity }}
        onDragEnd={handleCard1DragEnd}
      >
        <h2>Web Development Project</h2>
        <p>Build a responsive e-commerce site with integrated payment solutions and a custom CMS.</p>
        <p>Budget: $1500 - $2000</p>
        <p>Duration: 3 months</p>
      </motion.div>
      <motion.div
        className="card"
        style={{ x: card2X, y: card2Y, rotateX: card2RotateX, rotateY: card2RotateY, opacity: card2Opacity }}
      >
        <h2>Graphic Design Project</h2>
        <p>Create a visually appealing logo and branding materials for a new startup.</p>
        <p>Budget: $500 - $800</p>
        <p>Duration: 2 weeks</p>
      </motion.div>
    </div>
  );
};

export default SwipeProject;
