import React, { useState } from 'react';
import { motion, useMotionValue, useTransform, useAnimation } from 'framer-motion';

const SwipeProject = () => {
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const cards = [
    {
      title: 'Web Development Project',
      description: 'Build a responsive e-commerce site with integrated payment solutions and a custom CMS.',
      budget: '$1500 - $2000',
      duration: '3 months'
    },
    {
      title: 'Graphic Design Project',
      description: 'Create a visually appealing logo and branding materials for a new startup.',
      budget: '$500 - $800',
      duration: '2 weeks'
    },
    // Add more cards here
  ];

  const cardX = useMotionValue(0);
  const cardY = useMotionValue(0);

  const rotateX = useTransform(cardX, [-100, 100], [-25, 25]);
  const rotateY = useTransform(cardY, [-100, 100], [-25, 25]);
  const opacity = useTransform(cardX, [-100, 100], [1, 0.5]);

  const cardAnimation = useAnimation();

  const handleDragEnd = () => {
    if (Math.abs(cardX.get()) > 100) {
      cardAnimation.start({
        x: cardX.get() > 0 ? 1000 : -1000,
        transition: { type: "spring", stiffness: 300, damping: 30 }
      });

      setTimeout(() => {
        setCurrentCardIndex(currentCardIndex + 1);
        cardAnimation.start({
          x: 0,
          y: 0,
          transition: { type: "spring", stiffness: 300, damping: 30 }
        });
      }, 500);
    } else {
      cardAnimation.start({
        x: 0,
        y: 0,
        transition: { type: "spring", stiffness: 300, damping: 30 }
      });
    }
  };

  return (
    <div className="swipe-project">
      <h1>Swipe to Match with Projects</h1>
      <h3>Swipe left for rejecting and right for viewing more project details</h3>
      <motion.div
        className="card"
        drag
        dragConstraints={{ left: -100, right: 100, top: -100, bottom: 100 }}
        style={{ x: cardX, y: cardY, rotateX, rotateY, opacity }}
        onDragEnd={handleDragEnd}
      >
        <h2>{cards[currentCardIndex].title}</h2>
        <p>{cards[currentCardIndex].description}</p>
        <p>Budget: {cards[currentCardIndex].budget}</p>
        <p>Duration: {cards[currentCardIndex].duration}</p>
      </motion.div>
    </div>
  );
};

export default SwipeProject;
