// src/pages/SwipeProject.js
import React, { useState } from 'react';
import TinderCard from 'react-tinder-card';
import '../styles/SwipeProject.css';

const projectsData = [
  {
    id: 1,
    title: 'Web Development Project',
    description: 'Looking for a web developer to build a responsive e-commerce site with integrated payment solutions and a custom CMS.',
    budget: '$1500 - $2000',
    duration: '3 months',
    freelancer: {
      name: 'John Smith',
      rating: '4.8/5',
    },
  },
  {
    id: 2,
    title: 'Graphic Design Project',
    description: 'Seeking a graphic designer to create logos, business cards, and digital banners for a marketing campaign.',
    budget: '$500 - $700',
    duration: '1 month',
    freelancer: {
      name: 'Emma Brown',
      rating: '4.7/5',
    },
  },
  // Add more projects as needed
];

function SwipeProject() {
  const [projects, setProjects] = useState(projectsData);
  const [lastDirection, setLastDirection] = useState('');

  const swiped = (direction, projectTitle) => {
    setLastDirection(direction);
    // Remove the swiped project
    setProjects((prev) => prev.filter((project) => project.title !== projectTitle));
  };

  const outOfFrame = (projectTitle) => {
    console.log(`${projectTitle} left the screen!`);
  };

  return (
    <div className="swipe-project-container">
      {projects.length > 0 ? (
        projects.map((project) => (
          <TinderCard
            className="swipe"
            key={project.id}
            onSwipe={(dir) => swiped(dir, project.title)}
            onCardLeftScreen={() => outOfFrame(project.title)}
          >
            <div className="card">
              <h2>{project.title}</h2>
              <p>{project.description}</p>
              <div className="card-info">
                <span>Budget: {project.budget}</span>
                <span>Duration: {project.duration}</span>
              </div>
              <div className="freelancer-info">
                <img src="https://via.placeholder.com/50" alt="Freelancer" />
                <span>{project.freelancer.name}</span>
                <span>Rating: {project.freelancer.rating}</span>
              </div>
            </div>
          </TinderCard>
        ))
      ) : (
        <h2 className="finished-message">Projects are finished</h2>
      )}
      {lastDirection ? <h3 className="last-swipe">You swiped {lastDirection}</h3> : null}
    </div>
  );
}

export default SwipeProject;
